# Chudnovsky pi formula Works...

from decimal import Decimal, getcontext

# chat gpt code
n = int(input("enter the value of k:- "))
def chudnovsky(n):
    getcontext().prec = n + 1
    C = 426880 * Decimal(10005).sqrt()
    K, M, L, X, S = 6, 1, 13591409, 1, 13591409
    for k in range(1, n):
        M = (K**3 - 16*K) * M // k**3
        L += 545140134
        X *= -262537412640768000
        S += Decimal(M * L) / X
        K += 12
    pi = C / S
    return str(pi)[:n+2]


